query PaginationUserBookmarksOnFeed($first: Int, $after: PaginationCursor) {
  paginationUserBookmarks(first: $first, after: $after) {
    pageInfo {
      hasNextPage
      endCursor
    }
    edges {
      cursor
      node {
        id
        interest {
          id
          interest
        }
        tags {
          id
          tag
        }
       ...
      }
    }
  }
}
